package com.sc.study.jiuzhang.doublepoint;/** * @author yingqi * @date 2021/12/11 * 求全0子串 */public class AllZeroSubChar {    public static void main(String[] args) {        System.out.println(getAllZeroSub("001000"));    }    private static int getAllZeroSub(String origin) {        if (null == origin) {            return 0;        }        int i, j = 0;        int result = 0;        for (i = 0; i < origin.length(); i++) {            if (origin.charAt(i) != '0') {                continue;            }            j = Math.max(j, i + 1);            while (j < origin.length() && origin.charAt(j) == '0') {                j += 1;            }            result += j - i;        }        return result;    }}