package com.sc.study.jiuzhang.doublepoint;/** * @author yingqi * @date 2021/12/11 */public class TwoSumDiffByDoublePoint {    public static void main(String[] args) {        int[] result = doTwoSumDiffByDoublePoint(new int[]{1, 4, 6, 10}, 4);        System.out.println(result[0] + "," + result[1]);    }    public static int[] doTwoSumDiffByDoublePoint(int[] origin, int target) {        if (null == origin || origin.length <= 1) {            return new int[]{-1, -1};        }        target = Math.abs(target);        int j = 1;        for (int i = 0; i < origin.length; i++) {            j = Math.max(j, i + 1);            while (j < origin.length && origin[j] - origin[i] < target) {                j++;            }            if (j >= origin.length) {                break;            }            if (origin[j] - origin[i] == target) {                return new int[]{i, j};            }        }        return new int[]{-1, -1};    }}